mapper.update:
UPDATE car_payment_order
ecpay_trade_no = #{ecpayTradeNo}, payment_status = #{paymentStatus}, payment_time = #{paymentTime}, ecpay_status = #{ecpayStatus}, ecpay_status_desc = #{ecpayStatusDesc}, error_message = #{errorMessage}, del_flag = #{delFlag}, update_time = NOW()
WHERE id = #{id}

UPDATE car_payment_order
SET del_flag = 1, update_time = NOW()
WHERE id = #{id}
