mapper.select:
SELECT cs.id, cs.uid, cs.car_id as carId, cb.brand, COALESCE(cm.model, c.custom_model) as model, c.manufacture_year as manufactureYear, cs.sale_price as salePrice, cs.sale_title as saleTitle, cs.sale_description as saleDescription, cs.mileage, c.transmission, c.fuel_system as fuelSystem, c.color, cs.car_location_city as locationName, csp.photo_url as coverImage, cs.status, cs.id_garage as garageId FROM car_sales cs LEFT JOIN cars c ON c.id = cs.car_id LEFT JOIN car_brand cb ON cb.id = c.id_brand LEFT JOIN car_model cm ON cm.id = c.id_model LEFT JOIN car_sale_photos csp ON csp.car_sales_id = cs.id AND csp.is_cover = 1 WHERE cs.is_visible = 1 AND cs.status = '上架'
AND cb.brand IN
#{brandName}
AND (cm.model = #{model} OR c.custom_model = #{model})
AND c.manufacture_year = #{year}
AND cs.sale_price >= #{priceMin}
AND cs.sale_price <= #{priceMax}
AND c.transmission IN
#{trans}
AND c.drivetrain IN
#{drive}
AND c.fuel_system IN
#{fuel}
AND (cs.sale_title LIKE CONCAT('%', #{keyword}, '%') OR cs.sale_description LIKE CONCAT('%', #{keyword}, '%') OR cb.brand LIKE CONCAT('%', #{keyword}, '%') OR cm.model LIKE CONCAT('%', #{keyword}, '%') OR c.custom_model LIKE CONCAT('%', #{keyword}, '%'))
ORDER BY cs.sale_price ASC
ORDER BY cs.sale_price DESC
ORDER BY c.manufacture_year DESC
ORDER BY cs.c_dt DESC
