mapper.select:
SELECT * FROM car_sales cs 
WHERE cs.uid = #{uid} 
LIMIT 1

SELECT * FROM car_sales cs 
WHERE cs.id = #{id} 
LIMIT 1

mapper.update:
UPDATE car_sales 
SET uid = #{uid} 
WHERE id = #{id}

mapper.select:
select * from ( SELECT cs.* , concat(c.manufacture_year, ' ', cb.brand, ' ', case c.id_model when 0 then c.custom_model else cm.model end) as saleTitleShow, case when cr.id is null then 0 else 1 end as recommendedValue FROM car_sales cs left JOIN cars c on cs.car_id = c.id left join car_brand cb on c.id_brand = cb.id left join car_model cm on c.id_model = cm.id left join ${mgrDbName}.car_recommand cr on recommandType = 1 and cr.recommandId = cs.id
AND cs.salesperson LIKE CONCAT('%', #{entity.salesperson}, '%')
AND cs.status = #{entity.status}
AND cs.publisher LIKE CONCAT('%', #{entity.publisher}, '%')
AND cs.is_visible = #{entity.isVisible}
) t ORDER BY t.recommendedValue desc, t.c_dt DESC

